import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { motion } from 'framer-motion';
import { 
  LogOut, 
  Upload, 
  BarChart3, 
  Users, 
  ShoppingCart, 
  TrendingUp,
  Settings,
  Target
} from 'lucide-react';
import styled from 'styled-components';
import axios from 'axios';
import toast from 'react-hot-toast';
import KMeansVisualization from './KMeansVisualization';
import MarketBasketChart from './MarketBasketChart';
import StatsCards from './StatsCards';

// 🌐 API Base URL
const API_BASE = "http://127.0.0.1:5000/api";

/* ------------------- 🎨 Styled Components ------------------- */
const DashboardContainer = styled.div`
  min-height: 100vh;
  background: white;
  padding: 20px;
`;

/* ... keep your styled components the same ... */

const Dashboard = () => {
  const { user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('kmeans');
  const [loading, setLoading] = useState(false);
  const [dashboardStats, setDashboardStats] = useState(null);
  const [statsLoading, setStatsLoading] = useState(true); // new state for stats
  const [kmeansData, setKmeansData] = useState(null);
  const [marketBasketData, setMarketBasketData] = useState(null);

  const getAuthHeader = () => {
    const token = localStorage.getItem("token");
    return { Authorization: `Bearer ${token}` };
  };

  const fetchDashboardStats = async () => {
    try {
      setStatsLoading(true);
      const response = await axios.get(`${API_BASE}/dashboard-stats`, {
        headers: getAuthHeader(),
      });
      setDashboardStats(response.data);
    } catch (error) {
      console.error("Stats fetch error:", error.response || error);
      toast.error(error.response?.data?.message || 'Failed to fetch dashboard statistics');
    } finally {
      setStatsLoading(false);
    }
  };

  // ✅ Fetch stats on mount
  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;
    if (!file.name.endsWith('.csv')) {
      toast.error('Please upload a CSV file');
      return;
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post(`${API_BASE}/upload-data`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          ...getAuthHeader(),
        },
      });

      toast.success(response.data.message || 'Data uploaded successfully!');
      fetchDashboardStats(); // 🔄 Refresh stats after upload
    } catch (error) {
      console.error("Upload error:", error.response || error);
      toast.error(error.response?.data?.message || 'Failed to upload data');
    } finally {
      setLoading(false);
    }
  };

  const runKMeansAnalysis = async () => {
    setLoading(true);
    try {
      const { data } = await axios.post(`${API_BASE}/kmeans-analysis`, 
        { n_clusters: 5 }, 
        { headers: getAuthHeader() }
      );
      setKmeansData(data);
      toast.success('K-means analysis completed!');
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to run K-means analysis');
    } finally {
      setLoading(false);
    }
  };

  const runMarketBasketAnalysis = async () => {
    setLoading(true);
    try {
      const { data } = await axios.post(`${API_BASE}/market-basket-analysis`, {}, {
        headers: getAuthHeader(),
      });
      setMarketBasketData(data);
      toast.success('Market basket analysis completed!');
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to run market basket analysis');
    } finally {
      setLoading(false);
    }
  };

  return (
    <DashboardContainer>
      <Header>
        <Logo>
          <BarChart3 size={32} color="#667eea" />
          <h1>Market Basket Analysis</h1>
        </Logo>
        <UserSection>
          <UserInfo>
            <p className="username">Welcome, {user?.username}</p>
            <p className="role">Data Analyst</p>
          </UserInfo>
          <LogoutButton onClick={logout}>
            <LogOut size={16} />
            Logout
          </LogoutButton>
        </UserSection>
      </Header>

      {/* Dashboard Stats Section */}
      {statsLoading ? (
        <p>Loading dashboard stats...</p>
      ) : dashboardStats ? (
        <StatsCards stats={dashboardStats} />
      ) : (
        <p>No stats available. Upload some data!</p>
      )}

      {/* Main Content */}
      <MainContent>
        {/* Upload Section */}
        {/* ... keep as is ... */}

        {/* Controls Section */}
        {/* ... keep as is ... */}
      </MainContent>

      {/* Loading Spinner */}
      {loading && (
        <Card>
          <LoadingSpinner>
            <div className="spinner"></div>
          </LoadingSpinner>
        </Card>
      )}

      {/* Analysis Results */}
      {kmeansData && activeTab === 'kmeans' && <KMeansVisualization data={kmeansData} />}
      {marketBasketData && activeTab === 'marketbasket' && <MarketBasketChart data={marketBasketData} />}
    </DashboardContainer>
  );
};

export default Dashboard;
