import React, { useEffect, useState } from "react";
import { useAuth } from "../contexts/AuthContext";
import { motion } from "framer-motion";
import {
  LogOut,
  Upload,
  BarChart3,
  Users,
  ShoppingCart,
  TrendingUp,
  Settings,
  Target,
} from "lucide-react";
import styled from "styled-components";
import axios from "axios";
import toast from "react-hot-toast";
import KMeansVisualization from "./KMeansVisualization";
import MarketBasketChart from "./MarketBasketChart";
import StatsCards from "./StatsCards";
import TopCategoriesChart from "./TopCategoriesChart";

// 🌐 API Base URL
const API_BASE = "http://127.0.0.1:5000/api";

/* ------------------- 🎨 Styled Components ------------------- */
const DashboardContainer = styled.div`
  min-height: 100vh;
  background: white;
  padding: 20px;
`;

const Header = styled.header`
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(20px);
  border-radius: 16px;
  padding: 20px 32px;
  margin-bottom: 24px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
`;

const Logo = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;

  h1 {
    font-size: 1.8rem;
    font-weight: 700;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    margin: 0;
  }
`;

const UserSection = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
`;

const UserInfo = styled.div`
  text-align: right;

  .username {
    font-weight: 600;
    color: #374151;
    margin: 0;
  }

  .role {
    font-size: 0.875rem;
    color: #6b7280;
    margin: 0;
  }
`;

const LogoutButton = styled(motion.button)`
  background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
  color: white;
  border: none;
  border-radius: 8px;
  padding: 8px 16px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  font-weight: 500;
  transition: all 0.3s ease;
`;

const MainContent = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 24px;
  margin-bottom: 24px;

  @media (max-width: 1024px) {
    grid-template-columns: 1fr;
  }
`;

const Card = styled(motion.div)`
  background: rgba(255, 255, 255, 0.95);
  border-radius: 16px;
  padding: 24px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
`;

const CardHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;

  h2 {
    font-size: 1.25rem;
    font-weight: 600;
    color: #374151;
    margin: 0;
  }
`;

const ActionButton = styled(motion.button)`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  border-radius: 8px;
  padding: 8px 16px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  font-weight: 500;
`;

const UploadSection = styled.div`
  border: 2px dashed #d1d5db;
  border-radius: 12px;
  padding: 32px;
  text-align: center;
  cursor: pointer;
`;

const FileInput = styled.input`
  display: none;
`;

const AnalysisControls = styled.div`
  display: flex;
  gap: 12px;
  margin-bottom: 20px;
  flex-wrap: wrap;
`;

const ControlButton = styled(motion.button)`
  background: ${(props) =>
    props.active
      ? "linear-gradient(135deg, #667eea 0%, #764ba2 100%)"
      : "rgba(255, 255, 255, 0.8)"};
  color: ${(props) => (props.active ? "white" : "#374151")};
  border: 2px solid ${(props) => (props.active ? "transparent" : "#e5e7eb")};
  border-radius: 8px;
  padding: 8px 16px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const LoadingSpinner = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 200px;

  .spinner {
    width: 40px;
    height: 40px;
    border: 4px solid #f3f4f6;
    border-top: 4px solid #667eea;
    border-radius: 50%;
    animation: spin 1s linear infinite;
  }
`;

/* ------------------- 🧠 Component ------------------- */
const Dashboard = () => {
  const { user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState("kmeans");
  const [loading, setLoading] = useState(false);
  const [dashboardStats, setDashboardStats] = useState(null);
  const [kmeansData, setKmeansData] = useState(null);
  const [marketBasketData, setMarketBasketData] = useState(null);

  const getAuthHeader = () => {
    const token = localStorage.getItem("token");
    return { Authorization: `Bearer ${token}` };
  };

  // 📊 Fetch Dashboard Stats
  useEffect(() => {
    const fetchStats = async () => {
      try {
        const { data } = await axios.get(`${API_BASE}/dashboard-stats`, {
          headers: getAuthHeader(),
        });
        setDashboardStats(data);
      } catch (error) {
        console.error("Error fetching dashboard stats:", error);
        toast.error("Failed to load stats");
      }
    };

    fetchStats();
  }, []);

  // 📤 File Upload
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;
    if (!file.name.endsWith(".csv")) {
      toast.error("Please upload a CSV file");
      return;
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("file", file);

      const response = await axios.post(`${API_BASE}/upload-data`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          ...getAuthHeader(),
        },
      });

      toast.success(response.data.message || "Data uploaded successfully!");
    } catch (error) {
      console.error("Upload error:", error.response || error);
      toast.error(error.response?.data?.message || "Failed to upload data");
    } finally {
      setLoading(false);
    }
  };

  // 🧠 Run KMeans
  const runKMeansAnalysis = async () => {
    setLoading(true);
    try {
      const { data } = await axios.post(
        `${API_BASE}/kmeans-analysis`,
        { n_clusters: 5 },
        { headers: getAuthHeader() }
      );
      setKmeansData(data);
      toast.success("K-means analysis completed!");
    } catch (error) {
      toast.error(
        error.response?.data?.message || "Failed to run K-means analysis"
      );
    } finally {
      setLoading(false);
    }
  };

  // 🛒 Run Market Basket
  const runMarketBasketAnalysis = async () => {
    setLoading(true);
    try {
      const { data } = await axios.post(
        `${API_BASE}/market-basket-analysis`,
        {},
        {
          headers: getAuthHeader(),
        }
      );
      setMarketBasketData(data);
      toast.success("Market basket analysis completed!");
    } catch (error) {
      toast.error(
        error.response?.data?.message || "Failed to run market basket analysis"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <DashboardContainer>
      {/* -------- Header -------- */}
      <Header>
        <Logo>
          <BarChart3 size={32} color="#667eea" />
          <h1>Market Basket Analysis</h1>
        </Logo>
        <UserSection>
          <UserInfo>
            <p className="username">Welcome, {user?.username}</p>
            <p className="role">Data Analyst</p>
          </UserInfo>
          <LogoutButton onClick={logout}>
            <LogOut size={16} />
            Logout
          </LogoutButton>
        </UserSection>
      </Header>

      {/* -------- Stats -------- */}
      {dashboardStats ? (
        <>
          <StatsCards stats={dashboardStats} />
          <TopCategoriesChart topCategories={dashboardStats.top_categories} />
        </>
      ) : (
        <p>Loading dashboard stats...</p>
      )}

      {/* -------- Main Content -------- */}
      <MainContent>
        {/* Upload Section */}
        <Card>
          <CardHeader>
            <h2>Data Upload</h2>
            <Upload size={20} color="#6b7280" />
          </CardHeader>
          <UploadSection
            onClick={() => document.getElementById("fileInput").click()}
          >
            <Upload size={48} className="upload-icon" />
            <h3>Upload Transaction Data</h3>
            <p>Click to upload a CSV file with your transaction data</p>
            <ActionButton>
              <Upload size={16} />
              Choose File
            </ActionButton>
            <FileInput
              id="fileInput"
              type="file"
              accept=".csv"
              onChange={handleFileUpload}
            />
          </UploadSection>
        </Card>

        {/* Controls Section */}
        <Card>
          <CardHeader>
            <h2>Analysis Controls</h2>
            <Settings size={20} color="#6b7280" />
          </CardHeader>
          <AnalysisControls>
            <ControlButton
              active={activeTab === "kmeans"}
              onClick={() => setActiveTab("kmeans")}
            >
              <Users size={16} />
              K-Means
            </ControlButton>
            <ControlButton
              active={activeTab === "marketbasket"}
              onClick={() => setActiveTab("marketbasket")}
            >
              <ShoppingCart size={16} />
              Market Basket
            </ControlButton>
          </AnalysisControls>

          {activeTab === "kmeans" && (
            <ActionButton onClick={runKMeansAnalysis} disabled={loading}>
              <Target size={16} />
              Run K-Means Analysis
            </ActionButton>
          )}

          {activeTab === "marketbasket" && (
            <ActionButton onClick={runMarketBasketAnalysis} disabled={loading}>
              <TrendingUp size={16} />
              Run Market Basket Analysis
            </ActionButton>
          )}
        </Card>
      </MainContent>

      {/* -------- Loading -------- */}
      {loading && (
        <Card>
          <LoadingSpinner>
            <div className="spinner"></div>
          </LoadingSpinner>
        </Card>
      )}

      {/* -------- Results -------- */}
      {kmeansData && activeTab === "kmeans" && (
        <KMeansVisualization data={kmeansData} />
      )}
      {marketBasketData && activeTab === "marketbasket" && (
        <MarketBasketChart data={marketBasketData} />
      )}
    </DashboardContainer>
  );
};

export default Dashboard;
