// src/components/MarketBasket.js
import React, { useState } from "react";
import axios from "axios";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";

const MarketBasket = () => {
  const [rules, setRules] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const runMarketBasketAnalysis = async () => {
    setLoading(true);
    setError("");
    try {
      const token = localStorage.getItem("token"); // stored at login
      const res = await axios.post(
        "http://127.0.0.1:5000/api/market-basket-analysis",
        { min_support: 0.05, min_confidence: 0.3 }, // default params
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (res.data.association_rules) {
        setRules(res.data.association_rules);
      } else {
        setRules([]);
      }
    } catch (err) {
      console.error("MBA error:", err);
      setError("Failed to run Market Basket Analysis. Upload data first.");
      setRules([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 space-y-6">
      <h2 className="text-2xl font-bold">Market Basket Analysis</h2>

      {/* Run Button */}
      <Button onClick={runMarketBasketAnalysis} disabled={loading}>
        {loading ? "Running..." : "Run Market Basket Analysis"}
      </Button>

      {/* Error Handling */}
      {error && <p className="text-red-600">{error}</p>}

      {/* No Data */}
      {!loading && rules.length === 0 && !error && (
        <p className="text-gray-600">No Market Basket Data Available</p>
      )}

      {/* Rules Table */}
      {rules.length > 0 && (
        <Card>
          <CardContent>
            <h3 className="text-lg font-semibold mb-4">Association Rules</h3>
            <div className="overflow-x-auto">
              <table className="w-full border border-gray-300 rounded-lg">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="p-2 text-left">Antecedent</th>
                    <th className="p-2 text-left">Consequent</th>
                    <th className="p-2 text-center">Support</th>
                    <th className="p-2 text-center">Confidence</th>
                    <th className="p-2 text-center">Lift</th>
                  </tr>
                </thead>
                <tbody>
                  {rules.map((r, idx) => (
                    <tr key={idx} className="border-t">
                      <td className="p-2">{r.antecedent.join(", ")}</td>
                      <td className="p-2">{r.consequent.join(", ")}</td>
                      <td className="p-2 text-center">
                        {(r.support * 100).toFixed(2)}%
                      </td>
                      <td className="p-2 text-center">
                        {(r.confidence * 100).toFixed(2)}%
                      </td>
                      <td className="p-2 text-center">{r.lift.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default MarketBasket;
